var should = require("should")

describe("recoms", function () {
  describe(".buildMatrix", function () {
    it("should return success", function () {
      var recoms = require("../index.js")
      var data = [
        { id: 1, url: "http://gadget1.com", title: "Gadget 1", description: "Desc 1" }
      , { id: 2, url: "http://gadget2.com", title: "Gadget 2", description: "Desc 2" }
      , { id: 3, url: "http://gadget3.com", title: "Gadget 3", description: "Desc 3" }
      , { id: 4, url: "http://gadget4.com", title: "Gadget 4", description: "Desc 4" }
      , { id: 5, url: "http://gadget5.com", title: "Gadget 5", description: "Desc 5" }
      ]
      recoms.buildMatrix(data, function (results) {
        results.should.equal(7)
      })
    })
  })

  describe(".query", function () {
    it("should return success", function () {
      var recoms = require("../index.js")
      var data = [
        { id: 1, url: "http://gadget1.com", title: "Gadget 1", description: "Desc 1" }
      , { id: 2, url: "http://gadget2.com", title: "Gadget 2", description: "Desc 2" }
      , { id: 3, url: "http://gadget3.com", title: "Gadget 3", description: "Desc 3" }
      , { id: 4, url: "http://gadget4.com", title: "Gadget 4", description: "Desc 4" }
      , { id: 5, url: "http://gadget5.com", title: "Gadget 5", description: "Desc 5" }
      ]
      recoms.buildMatrix(data, function (results) {
        results.should.equal(7)
      })

      var query = "query"

      recoms.query(query, function (results) {
        results.should.equal(7)
      })
    })

  })
})
